import React from 'react';
import './Home.css';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div className="home">
      <div className="hero">
        <h1>Welcome to Global Times</h1>
        <p>Your source for the latest news and insights</p>
        <Link to="/latest" className="cta-button">Explore Latest News</Link> {/* Call-to-Action Button */}
      </div>
      <div className="introduction">
        <h2>About Us</h2>
        <p>
          Stay updated with the most recent headlines, in-depth analysis, and
          exclusive reports from around the world. Explore the latest in
          technology, sports, entertainment, and more.
        </p>
        <p>
          Share your own story on the <Link to="/add">Add News</Link> page. {/* Encouragement to add news */}
        </p>
      </div>
    </div>
  );
};

export default Home;
